import OpenAI from "openai";
import dotenv from "dotenv";

dotenv.config();

const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY,
});

async function run() {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [
        { role: "system", content: "אתה סוכן עוזר של רשת המסעדות טומי. דבר תמיד בעברית." },
        { role: "user", content: "שלום, אני רוצה להזמין פיצה משפחתית עם תוספות." }
      ],
    });

    console.log("תשובת GPT-5:", response.choices[0].message.content);
  } catch (error) {
    console.error("❌ שגיאה:", error.message);
  }
}

run();
